var words = {}; //Obj with splitted words
var counter = {}; //Obj with all words and how many repeats
var sortable = []; //Sorted counter in format array of {word, counter}
let res = {}; //Return value


// Parse keywords
const splitWords = (str, i, id) => {
    let w = 1;
    let k = 0;
    words[i] = {};
    while (str[k]) {
        if (str[k] === ";") {
            counter[words[i][w]] = (counter[words[i][w]] || 0) + 1;
            k++;
            w++;
        }
        words[i][w] = (words[i][w] || '') + str[k];
        k++;
    }
    words[i]["id"] = id;
}
// sorting from high to low
const Sort = (counter) => {    
for (w in counter) {
    sortable.push([w, counter[w]]);
    }
    sortable.sort((a, b) => {
        return b[1] - a[1];
    });
}
// Create obj with keyword to prevent errors
const createObj = (w) => {
    if (res[w])
        return;
    else
        res[w] = {};
}

//Finding exact article in example
const findArticle = (str) => {
    const id = str["id"];
    for (k in example) {
        if (example[k]["pmid"] === id)
            return example[k];
    }
}
// Checking is article already in res obj
const checkArticle = (art) => {
    for (k in res) {
        for (j in res[k]) {
            if (res[k][j] === art)
                return true;
        }
    }
    return false;
}

const sortedRes = () => {
    let i = 0; //variable to able add multi obj to one word exmaple: {"coronavirus": {{i: "..."}, {i + 1: "..."}, {i + 2: "..."}}
    let z = 0; //variable to loop thru sortable
    res['Other'] = {};
    while (sortable[z]) {
        if (z >= 29) { 
            for (k in words) {
                for (j in words[k]) {
                    let art = findArticle(words[k] || "");
                    if (art) {
                     if (!checkArticle(art)) {
                         res['Other'][i] = art;
                        }
                    }
                    i++;
                }
            }
        }
        else
            for (k in words) {
                for (j in words[k]) {
                    if (!words[k])
                        break;
                    let w = words[k][j];
                    if (words[k][j] === sortable[z][0]) {
                        let art = findArticle(words[k]);
                        if (!checkArticle(art)) {
                            createObj(w);
                            res[w][i] = art;
                        }
                        i++;
                    }
                }
            }
        z++;
    }
}

// Begining
